import React, { useEffect, useState } from 'react';
import { NavLink } from 'react-router-dom';
import axios from 'axios';

const RegisterPage = () => {

    const [firstname, setFirstName] = useState([]);
    const [lastname, setLastName] = useState([]);
    const [email, setEmail] = useState([]);
    const [password, setPassword] = useState([]);

    const [list,setList] = useState([]);

    useEffect(() => {
        axios.get('http://localhost:3001/api/get').then((response) => {
            setList(response.data);
            // console.log(response.data);
            // alert(response);
        });
    }, []);

    const Register = () =>{
        axios.post("http://localhost:3001/registerpage", {
            firstname: firstname,
            lastname: lastname,
            email: email,
            password: password,
        }).then((response) => {
            console.log(response.data);
            // alert(response);
        });
    };

    return (
        <div className="container col-md-5 mt-4 p-5 bg-info card shadow rounded">
            <div className="text-center mb-2">
                <h5>Login Here</h5>
            </div>
            <form >
                <div class="form-group mb-3">
                    <input type="text" 
                    class="form-control" 
                    placeholder="First Name *" required 
                    onChange={(e) => {
                        setFirstName(e.target.value);
                    }} />
                </div>
                <div class="form-group mb-3">
                    <input type="text" 
                    class="form-control" 
                    aria-pressed="false" 
                    autocomplete="off" 
                    placeholder="Last Name *" 
                    onChange={(e) => {
                        setLastName(e.target.value);
                    }}/>
                </div>
                <div class="form-group mb-3">
                    <input type="email" 
                    class="form-control" 
                    aria-pressed="false" 
                    autocomplete="off" 
                    placeholder="Your Email *" 
                    onChange={(e) => {
                        setEmail(e.target.value);
                    }}/>
                </div>
                <div class="form-group mb-3">
                    <input type="password" 
                    class="form-control" 
                    placeholder="Password *" 
                    pattern="(?=.*\d)(?=.*[a-z])(?=.*[A-Z]).{8,}" 
                    autocomplete="off" 
                    required />
                    <span ></span>
                </div>
                <div class="form-group mb-3">
                    <input type="text" 
                    class="form-control" 
                    placeholder="Confirm Password *" 
                    onChange={(e) => {
                        setPassword(e.target.value);
                    }} required />
                    <span id='message'></span>
                </div>
                <div class="btn-toolbar justify-content-between mt-4" role="toolbar" aria-label="Toolbar with button groups">
                    <button type="submit" 
                    class="btn btn-success"
                    onClick={Register}>Register</button>
                    <div class="text-group-prepend">
                        <label>Already User? <NavLink className="btn btn-primary" exact to="./loginpage" >Login</NavLink>
                        </label>
                    </div>
                </div>
            </form>
            {
                list.map((val) => {
                    return <h1>Name : {val.name} || Password: {val.password}</h1>
                })
            }
            
        </div>
    )
}
export default RegisterPage;
