import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { NavLink, Redirect } from 'react-router-dom';
import LoginPage from './loginpage';

const HomePage = () => {

    // const [list, setList] = useState([]);
    const [createpost,createPost] = useState([]);
    const [createdpost,setCreatedPost] = useState([]);
    const [createdpostid,setCreatedPostId] = useState([]);
    
    const Post = () => {
        if(localStorage.getItem('user')) {
            let username = localStorage.getItem('user');
            axios.post('http://localhost:3001/homepage',{
                post: createpost,
                username: username
            }).then((response) => {
                if(response.data.id) {
                    // alert("Success");
                    setCreatedPost(createpost);
                    setCreatedPostId(response.data.id);
                }   
            });
        }
        else {
            alert('Login to post');
        }
    }

    const [comment,setComment] = useState([]);
    const AddComment = () => {
        if(localStorage.getItem('user')){
            let username = localStorage.getItem('user');
            axios.post('http://localhost:3001/homepage:bh',{
                postid: createdpostid,
                username: username,
                comment: comment
            }).then((response) =>{
                if(response.data.message) {
                    alert(response.data.message);
                    // setShowPost(createpost);
                }   
            });
            // alert(createdpostid);
        }
        else{
            alert('Login first!')
        }
    }
    const [post,setPost] = useState([]);
    useEffect(() => {
        const postid = 1;
        axios.get('http://localhost:3001/homepage',
        createdpostid
        ).then((response) => {
            setPost(response.data);
        });
    }, []);

    return (
        <div className="container group col-md-7 mt-5 mb-5 p-3 bg-info card shadow rounded">
            <div className="form-group row">
                <div className="row mt-4">
                    <div className="col-10">
                        <input type="text" 
                        className="form-control" 
                        placeholder="Add your post here.." 
                        onChange={(e) =>{
                            createPost(e.target.value);
                        }}/>
                    </div>
                    <div className="col-2">
                        <button className="btn btn-primary" onClick={Post}>Post</button>
                    </div>
                </div>
                <div className="col-md-7">
                    <div className="mb-3 mt-3">
                        <div>
                            <h5>Showing Post</h5>
                        </div>
                        <div className="bg-secondary card">
                            <h4 key={createdpostid}>{createdpost}</h4>
                            {/* {
                                 post.map((post,index) => {
                                    if(index==0) 
                                        return <h4 key={post.toString()}>{post.firstname}</h4>
                                })
                            } */}
                        </div>
                    </div>
                    <div className="mb-3 mt-3">
                        <div>
                            <h6>Comment Here</h6>
                        </div>
                        <div className="row">
                            <div className="col-12 col-sm-6 col-md-8">
                                <input type="text" 
                                className="form-control" 
                                placeholder="Add comment.."
                                onChange={(e) =>{
                                    setComment(e.target.value);
                                }} />
                            </div>
                            <div className="col-6 col-md-2">
                                <button type="button" 
                                className="btn btn-primary"
                                onClick={AddComment}>Add</button>
                            </div>
                        </div>
                    </div>
                </div>
                <div className="col-sm-5 ">
                <h6 className="mt-4">Showing Comments</h6>
                    <div className=" mt-1 p-1 card">
                        {
                            post.map((val,index) => {
                                return (
                                <div>
                                    <div>{index+1}: {post.post}</div>
                                </div>
                                )
                            })
                        }
                    </div>
                </div>
            </div>
        </div>
    )
}

export default HomePage;
