import React, { useEffect, useState } from 'react';
import { NavLink } from 'react-router-dom';
import axios from 'axios';

const RegisterPage = () => {

    const [firstname, setFirstName] = useState([]);
    const [lastname, setLastName] = useState([]);
    const [email, setEmail] = useState([]);
    const [password, setPassword] = useState([]);

    const [success,setSuccess] = useState([]);

    const Register = () =>{
        axios.post("http://localhost:3001/registerpage", {
            firstname: firstname,
            lastname: lastname,
            email: email,
            password: password,
        }).then((error,response) => {
            if(error) {
                alert("jkdkff");
                // setSuccess("success");
            }
            alert("Success");
        });
    };

    const Success = () => {
        if(success == 'Something wrong') {
            return <h6 className="text-danger">Error!! Register again</h6>
        } else if(success == 'success') {
            return <h6 className="text-green">Registered Successfully..</h6>
        }
    }

    return (
        <div className="container col-md-5 mt-4 p-5 bg-info card shadow rounded">
            <div className="text-center mb-2">
                <h5>Registration Page</h5>
            </div>
            <form >
                {Success()}
                <div className="form-group mb-3">
                    <input type="text" 
                    className="form-control" 
                    placeholder="First Name *" required 
                    onChange={(e) => {
                        setFirstName(e.target.value);
                    }} />
                </div>
                <div className="form-group mb-3">
                    <input type="text" 
                    className="form-control" 
                    aria-pressed="false" 
                    autoComplete="off" 
                    placeholder="Last Name *" 
                    onChange={(e) => {
                        setLastName(e.target.value);
                    }}/>
                </div>
                <div className="form-group mb-3">
                    <input type="email" 
                    className="form-control" 
                    aria-pressed="false" 
                    autoComplete="off" 
                    placeholder="Your Email *" 
                    onChange={(e) => {
                        setEmail(e.target.value);
                    }}/>
                </div>
                <div className="form-group mb-3">
                    <input type="password" 
                    className="form-control" 
                    placeholder="Password *" 
                    pattern="(?=.*\d)(?=.*[a-z])(?=.*[A-Z]).{8,}" 
                    autoComplete="off" 
                    required />
                    <span ></span>
                </div>
                <div className="form-group mb-3">
                    <input type="text" 
                    className="form-control" 
                    placeholder="Confirm Password *" 
                    onChange={(e) => {
                        setPassword(e.target.value);
                    }} required />
                    <span id='message'></span>
                </div>
                <div className="btn-toolbar justify-content-between mt-4" role="toolbar" aria-label="Toolbar with button groups">
                    <button type="submit" 
                    className="btn btn-success"
                    onClick={Register}>Register</button>
                    <div className="text-group-prepend">
                        <label>Already User? <NavLink className="btn btn-primary" exact to="./loginpage" >Login</NavLink>
                        </label>
                    </div>
                </div>
            </form>
        </div>
    )
}
export default RegisterPage;
