import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { NavLink } from 'react-router-dom';

const HomePage = () => {

    const [list, setList] = useState([]);

    useEffect(() => {
        axios.get('http://localhost:3001/api/get').then((response) => {
            setList(response.data);
        });
    }, []);

    return (
        <div className="container group col-md-7 mt-5 mb-5 p-3 bg-info card shadow rounded">
            <div className="form-group row">
                <div className="row">
                    
                    <div className="col-8">
                        <input type="text" className="form-control" placeholder="Add your post here.." />
                    </div>
                    <div className="col-4">
                        <h4>Add Post</h4>
                    </div>
                </div>
                <div className="col-md-7">
                    <div className="mb-3 mt-3">
                        <div>
                            <h4>Showing Post</h4>
                        </div>
                        <div className="card">
                            {
                                list.map((post,index) => {
                                    if(index==0)
                                    return <h4 key={post.toString()}>{post.firstname}</h4>
                                })
                            }
                        </div>
                    </div>
                    <div className="mb-3 mt-3">
                        <div>
                            <h4>Comment Here</h4>
                        </div>
                        <div className="row">
                            <div className="col-12 col-sm-6 col-md-8">
                                <input type="text" className="form-control" placeholder="add coment" />
                            </div>
                            <div className="col-6 col-md-2">
                                <NavLink type="button" to="/" className="btn btn-primary">add</NavLink>
                            </div>
                        </div>
                    </div>
                </div>
                <div className="col-sm-5 ">
                <h6 className="mt-4">Showing Total {list.length} Comments</h6>
                    <div className=" mt-2 p-1 card">
                        {
                            list.map((val,index) => {
                                return (
                                <div>
                                    <div>{index+1}: {val.email}</div>
                                </div>
                                )
                            })
                        }
                    </div>
                </div>
            </div>
        </div>
    )
}

export default HomePage;
