import React, { useEffect, useState } from 'react';
import { NavLink } from 'react-router-dom';
import axios from 'axios';
import NavbarPage from './navbarpage';

const SaveOrder = () => {

    const [userId, setUserId] = useState([]);
    const [subTotal, setSubTotal] = useState([]);
    const [phoneNo, setPhoneNo] = useState([]);

    const Register = () =>{
        axios.post("http://localhost:3001/add-user", {
            userId: userId,
            subTotal: subTotal,
            phoneNo: phoneNo
        }).then((response) => {
            if(response.data.message) {
                alert(response.data.message);
            } 
            else {
                alert("error");
            }
        })  
    };

    return (
        <div className="container col-md-5 mt-5 p-5 bg-info card shadow rounded">
            <div className="text-center mb-2">
                <h5>Add Order Page</h5>
            </div>
            <form>
                <div className="form-group mb-3">
                    <input type="number" 
                    className="form-control" 
                    placeholder="User_Id *" required 
                    onChange={(e) => {
                        setUserId(e.target.value);
                    }} />
                </div>
                <div className="form-group mb-3">
                    <input type="number" 
                    className="form-control" 
                    aria-pressed="false" 
                    autoComplete="off" 
                    placeholder="Sub Total *"  required
                    onChange={(e) => {
                        setSubTotal(e.target.value);
                    }}/>
                </div>
                <div className="form-group mb-3">
                    <input type="text" 
                    className="form-control" 
                    placeholder="Phone No *" 
                    autoComplete="off" required 
                    onChange={(e) => {
                        setPhoneNo(e.target.value);
                    }}/>
                </div>
                <div className="btn-toolbar justify-content-between mt-4" role="toolbar" aria-label="Toolbar with button groups">
                    <button type="submit" className="btn btn-success" onClick={SaveOrder}> Save Order </button>
                    {/* <div className="text-group-prepend">
                        <label>Already User? 
                            <NavLink className="btn btn-primary" exact to="./login-user" >Login</NavLink>
                        </label>
                    </div> */}
                </div>
            </form>
        </div>
    )
}
export default SaveOrder;
