import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { NavLink, Redirect } from 'react-router-dom';
import LoginPage from './loginpage';
import NavbarPage from './navbarpage';

const HomePage = () => {

    // adding post into database
    const [createpost, createPost] = useState([]);
    const [createdpostid,setCreatedPostId] = useState([]);
    const Post = () => {
        if (localStorage.getItem('user')) {
            let username = localStorage.getItem('user');
            axios.post('http://localhost:3001/homepage/addpost', {
                post: createpost,
                username: username
            }).then((response) => {
                if (response.data.id) {
                    alert("Success");
                    // setCreatedPost(createpost);
                    setCreatedPostId(response.data.id);
                }
            });
            window.location.reload(false);
        }
        else {
            alert('Login to post');
        }
    }
    // adding comment into database
    const [comment, setComment] = useState([]);
    const AddComment = (postid) => {
        // console.log(postkid,comment);
        if(localStorage.getItem('user')) {
            let username = localStorage.getItem('user');
            axios.post('http://localhost:3001/homepage/addcomment', {
                postid: postid,
                username: username,
                comment: comment
            }).then((response) => {
                if (response.data.message) {
                    alert(response.data.message);
                    // setShowPost(createpost);
                }
            });
        }
        else {
            alert('Login first!')
        }
    }

    const [showusers, setShowUsers] = useState([]);
    const [showusers1, setShowUsers1] = useState([]);
    useEffect(() => {
        axios.get('http://localhost:3001/get-users').then((response) => {
            setShowUsers(response.data);
            setShowUsers1(response.data);
        });
    },[]);

    const [showcomment, setShowComment] = useState([]);
    const ShowComment = (postid) =>{
        // console.log(postid);
        axios.post('http://localhost:3001/homepage/showcomment',
        {PostId: postid}
        ).then((response) => {
            if(response.data <=0 ) alert("no comment on this post");
            setShowComment(response.data);
        });
    }

    return (
        
        // <div className="container col-md-12 mt-5 mb-2 p-3">
        //     <div class="row">
        //         <div class="col-sm-6">
        //             <div class="card shadow">
        //                 <div class="card-body">
        //                     <h5 class="card-title">Special title treatment</h5>
        //                     <p class="card-text">With supporting text below as a natural lead-in to additional content.</p>
        //                     <a href="#" class="btn btn-primary">Go somewhere</a>
        //                 </div>
        //             </div>
        //         </div>
        //         <div class="col-sm-6">
        //             <div class="card shadow">
        //                 <div class="card-body">
        //                     <h5 class="card-title">Special title treatment</h5>
        //                     <p class="card-text">With supporting text below as a natural lead-in to additional content.</p>
        //                     <a href="#" class="btn btn-primary">Go somewhere</a>
        //                 </div>
        //             </div>
        //         </div>
        //     </div>
        // </div>
        <div className="container col-md-8 mt-5 mb-2 p-3 ">
            <div className="card row shadow rounded">
                <div className="col-md-12">
                    <table class="table">
                        <thead>
                            <tr>
                                <th scope="col">User_Id</th>
                                <th scope="col">First</th>
                                <th scope="col">Phone No</th>
                            
                            </tr>
                        </thead>
                        <tbody>
                            {
                                showusers.map((val) => {
                                    return (
                                        <tr>
                                            <th scope="row">{val.User_Id}</th>
                                            <td>{val.Name}</td>
                                            <td>{val.Phone_No}</td>
                                        </tr>
                                    )
                                })
                            }
                        </tbody>
                    </table>
                </div>
            </div>
        </div>
    )
}
export default HomePage;
