import React, { useEffect, useState } from 'react';
import axios from 'axios';

const HomePage = () => {
    const [showusers, setShowUsers] = useState([]);
    const [name,setName] = useState([]);
    const [phoneNo,setPhoneNo] = useState([]);
    const [subTotal,setSubTotal] = useState([]);
    useEffect(() => {
        axios.get('http://localhost:3001/get-users').then((response) => {
            setShowUsers(response.data);
        });
    },[]);

    const DisplayOrder = (User_Id,name) =>{
        setName(name);
        axios.get(`http://localhost:3001/get-order/${User_Id}`).then((response) =>{
            console.log(response.data);
            setPhoneNo(response.data[0].Phone_No);
            setSubTotal(response.data[0].Sub_Total)
        })
    }
    return (
        // <div className="container col-md-8 mt-5 mb-2 p-3 ">
        //     <div className="card row shadow rounded">
        //         <div className="col-md-8">
                    // <table className="table">
                    //     <thead>
                    //         <tr>
                    //             <th scope="col">User_Id</th>
                    //             <th scope="col">Name</th>
                    //             <th scope="col">Phone No</th>
                    //             <th scope="col">Order Details</th>
                    //         </tr>
                    //     </thead>
                    //     <tbody>
                    //         {
                    //             showusers.map((val) => {
                    //                 return (
                    //                     <tr>
                    //                         <th scope="row">{val.User_Id}</th>
                    //                         <td>{val.Name}</td>
                    //                         <td>{val.Phone_No}</td>
                    //                         <td><a className='btn btn-success btn-sm' type='button' onClick={()=> DisplayOrder(val.User_Id)}>Get Details</a> </td>
                    //                     </tr>
                    //                 )
                    //             })
                    //         }
                    //     </tbody>
                    // </table>
        //         </div>
        //     </div>
        // </div>
        <div className="container col-md-10 mt-5 mb-2 p-3">
            <div class="row">
                <div class="col-sm-8">
                    <div class="card">
                        <div class="card-body">
                            <h5 class="card-title">All Users</h5>
                            <table className="table">
                                <thead>
                                    <tr>
                                        <th scope="col">User_Id</th>
                                        <th scope="col">Name</th>
                                        <th scope="col">Phone No</th>
                                        <th scope="col">Order Details</th>
                                    </tr>
                                </thead>
                                <tbody>
                                    {
                                        showusers.map((val) => {
                                            return (
                                                <tr>
                                                    <th scope="row">{val.User_Id}</th>
                                                    <td>{val.Name}</td>
                                                    <td>{val.Phone_No}</td>
                                                    <td><a className='btn btn-success btn-sm' type='button' onClick={()=> DisplayOrder(val.User_Id,val.Name)}>Get Details</a> </td>
                                                </tr>
                                            )
                                        })
                                    }
                                </tbody>
                            </table>
                        </div>
                    </div>
                </div>
                <div class="col-sm-4">
                    <div class="card">
                        <div class="card-body">
                            <h5 class="card-title">User Order Details</h5>
                            <br/>
                            <p>User Name: {name}</p>
                            <p>Phone No: {phoneNo}</p>
                            <p>Sub Total: <b>{subTotal}</b></p>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    )
}
export default HomePage;
