import React, { useEffect, useState } from 'react';
import axios from 'axios';

const HomePage = () => {
    const [showusers, setShowUsers] = useState([]);
    //const [showusers1, setShowUsers1] = useState([]);
    useEffect(() => {
        axios.get('http://localhost:3001/get-users').then((response) => {
            setShowUsers(response.data);
            //setShowUsers1(response.data);
        });
    },[]);

    const DisplayOrder = (User_Id) =>{
        //alert("hii");
        console.log('We need to get the details for ', User_Id);
    }
    // const [showcomment, setShowComment] = useState([]);
    // const ShowComment = (postid) =>{
    //     // console.log(postid);
    //     axios.post('http://localhost:3001/homepage/showcomment',
    //     {PostId: postid}
    //     ).then((response) => {
    //         if(response.data <=0 ) alert("no comment on this post");
    //         setShowComment(response.data);
    //     });
    // }

    return (
        <div className="container col-md-8 mt-5 mb-2 p-3 ">
            <div className="card row shadow rounded">
                <div className="col-md-12">
                    <table className="table">
                        <thead>
                            <tr>
                                <th scope="col">User_Id</th>
                                <th scope="col">Name</th>
                                <th scope="col">Phone No</th>
                            </tr>
                        </thead>
                        <tbody>
                            {
                                showusers.map((val) => {
                                    return (
                                        <tr>
                                            <th scope="row">{val.User_Id}</th>
                                            <td>{val.Name}</td>
                                            <td>{val.Phone_No}</td>
                                            <td><a type='button' onClick={()=> DisplayOrder(val.User_Id)}>Get Details</a> </td>
                                        </tr>
                                    )
                                })
                            }
                        </tbody>
                    </table>
                </div>
            </div>
        </div>
    )
}
export default HomePage;
