import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { NavLink, Redirect } from 'react-router-dom';

const HomePage = () => {

    const [list, setList] = useState([]);
    const [createpost,createPost] = useState([]);
    const [setpost,setPost] = useState([]);
    const Post = () =>{
        if(localStorage.getItem('user')) {
            axios.post('http://localhost:3001/homepage',{
                post: createpost,
                username: localStorage.getItem('user')
            }).then((response) =>{
                setPost(createpost);
            })
        }
        else {
            alert('Login to post');
            return <Redirect to="/loginpage" />
        }

    }

    useEffect(() => {
        axios.get('http://localhost:3001/api/get').then((response) => {
            setList(response.data);
        });
    }, []);

    return (
        <div className="container group col-md-7 mt-5 mb-5 p-3 bg-info card shadow rounded">
            <div className="form-group row">
                <div className="row mt-4">
                    <div className="col-10">
                        <input type="text" 
                        className="form-control" 
                        placeholder="Add your post here.." 
                        onChange={(e) =>{
                            setPost(e.target.value);
                        }}/>
                    </div>
                    <div className="col-2">
                        <button className="btn btn-primary" onClick={Post}>Post</button>
                    </div>
                </div>
                <div className="col-md-7">
                    <div className="mb-3 mt-3">
                        <div>
                            <h5>Showing Post</h5>
                        </div>
                        <div className="card">
                            {
                                list.map((post,index) => {
                                    if(index==0)
                                    return <h4 key={post.toString()}>{post.firstname}</h4>
                                })
                            }
                        </div>
                    </div>
                    <div className="mb-3 mt-3">
                        <div>
                            <h6>Comment Here</h6>
                        </div>
                        <div className="row">
                            <div className="col-12 col-sm-6 col-md-8">
                                <input type="text" className="form-control" placeholder="add coment" />
                            </div>
                            <div className="col-6 col-md-2">
                                <NavLink type="button" to="/" className="btn btn-primary">add</NavLink>
                            </div>
                        </div>
                    </div>
                </div>
                <div className="col-sm-5 ">
                <h6 className="mt-4">Showing Total {list.length} Comments</h6>
                    <div className=" mt-2 p-1 card">
                        {
                            list.map((val,index) => {
                                return (
                                <div>
                                    <div>{index+1}: {val.email}</div>
                                </div>
                                )
                            })
                        }
                    </div>
                </div>
            </div>
        </div>
    )
}

export default HomePage;
